import { useState, useEffect } from "react"
import axios from "axios"

function App() {
  const url = 'http://localhost:5000'
  const [todos, setTodos] = useState([])

  useEffect(() => {
    getTodos()
  }, [todos])

  const getTodos = async () => {
    const response = await axios.get(`http://localhost:5000/api/todo`)
    setTodos(response.data.data)
  }

  return (
    <div className="flex flex-col pt-24 gap-6 w-10/12 mx-auto items-center justify-center">
      <h1 className="text-5xl font-bold tracking-wide">ToDo List</h1>
      <form className="flex items-center mt-4" >
        <input className="bg-gray-200 rounded-md p-3 focus:outline-none font-semibold" type="text" />
        <button className="p-2 rounded-md font-semibold bg-black text-white border-2 border-black hover:bg-white hover:text-black transition-all">Add</button>
      </form>
      <ul className="py-5">
        {todos.map((todo, index) => {
          return <li className="py-2 border-b border-gray-200">{}</li>
        })}

        <li>b</li>
      </ul>
    </div>
  );
}

export default App;
